$(function(){$(".del").click(function(a){var b=$(a.target),c=b.data("id"),d=$(".item-id-"+c);$.ajax({type:"DELETE",url:"/admin/movie/list?id="+c}).done(function(a){1===a.success&&d.length>0&&d.remove()})}),$("#douban").blur(function(){var a=$(this),b=a.val();$.ajax({url:"https://api.douban.com/v2/movie/"+b,cache:!0,type:"get",dataType:"jsonp",crossDomain:!0,jsonp:"callback",success:function(a){$("#inputTitle").val(a.title),$("#inputDoctor").val(a.attrs.director[0]),$("#inputCountry").val(a.attrs.country),$("#inputLanguage").val(a.attrs.language),$("#inputPoster").val(a.image),$("#inputYear").val(a.attrs.year),$("#inputSummary").val(a.summary)}})})});